mapper.select:
	      SELECT * 
	      FROM member 
	      where u_id=#{u_id}

    	  AND u_pw=#{u_pw}

mapper.update:
 UPDATE member 
 SET u_pw=#{u_pw,jdbcType=VARCHAR}
	 ,u_nick=#{u_nick,jdbcType=VARCHAR}
 	 ,u_addr=#{u_addr,jdbcType=VARCHAR}
 	 ,u_email=#{u_email,jdbcType=VARCHAR}
 	 ,u_phone=#{u_phone,jdbcType=VARCHAR}
 where u_id=#{u_id,jdbcType=VARCHAR}
