mapper.update:
 	UPDATE shop_reg 
 	SET  s_name=#{s_name}
	 	,s_addr=#{s_addr}
 	 	,s_phone=#{s_phone}
 	 	,s_hour=#{s_hour}
 	 	,delibery=#{delibery}
 	where u_id=#{u_id}

mapper.select:
	select distinct img , meat , price, s_name, s_addr 
from	
		(select distinct mr.img , mr.meat , mr.price, sr.s_name, substr(sr.s_addr,1,2) s_addr from menu_reg mr inner join shop_reg sr on mr.s_num=sr.s_num 
		 where  

		 		substr(sr.s_addr,1,2) in #{item}
		)

where 
		meat in#{item}
